// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

import "@openzeppelin/contracts/payment/escrow/Escrow.sol";

contract ProductEscrow {
    mapping(uint256 => mapping(address => uint256)) private _deposits;

    function deposit(uint256 productId, address depositor) public payable {
        _deposits[productId][depositor] += msg.value;
    }

    function release(uint256 productId, address payable beneficiary) public {
        uint256 amount = _deposits[productId][beneficiary];
        require(amount > 0, "No funds to release");
        beneficiary.transfer(amount);
        _deposits[productId][beneficiary] = 0;
    }

    function depositsOf(uint256 productId, address depositor) public view returns (uint256) {
        return _deposits[productId][depositor];
    }
}
